import React, { useEffect, useState } from 'react'
import "./Sports.css";

const Sports = () => {
    const [fsport,setfsport]=useState(localStorage.getItem("sport") || "");
    const [fplayer,setfplayer]=useState(localStorage.getItem("player") || "");
    const [player, setPlayer] = useState([]);

    const Players = {
        Cricket: ["Dhoni", "Virat Kohli", "Rohit Sharma"],
        Football: ["Messi", "Ronaldo", "Neymar"],
        Basketball: ["Jordan", "LeBron", "Kobe"],
      };
    // console.log(fsport)
    // console.log(fplayer)
    useEffect(()=>{
        if (fsport && Players[fsport]) {
            setPlayer(Players[fsport]);
            if (!Players[fsport].includes(fplayer)) {
                setfplayer(""); 
            }
        } else {
            setPlayer([]);
            setfplayer("");
        }
    },[fsport])
    console.log(player)
    // useEffect(() => {
    //     localStorage.setItem("sport", fsport);
    //     localStorage.setItem("player", fplayer);
    //   }, [fsport, fplayer]);
    // useEffect(()=>{
    //     document.getElementById('sportsname').value=localStorage.getItem("sport")
    //     document.getElementById('playername').value=localStorage.getItem("player")   
    // },[])
    
    
  return (
    <div className='main'>
        <div className='box'>
                <label>Favorite Sports</label>
                <input type='text'  id='sportsname' value={fsport} placeholder='Select Your Favourite Game' readOnly></input>
                <label>Favorite Player</label>
                <input type='text'  id='playername' value={fplayer} placeholder='Select Your Favourite player' readOnly></input>
                <select id='fsprt' value={fsport} onChange={(e)=>{setfsport(e.target.value);localStorage.setItem("sport",e.target.value)}}>
                    <option value={""}>Sports</option>
                    <option value={"Cricket"}>Cricket</option>
                    <option value={"Football"}>Football</option>
                    <option value={"Basketball"}>Basketball</option>
                </select>
                <select id='fplyr' value={fplayer} onChange={(e)=>{setfplayer(e.target.value); localStorage.setItem("player",e.target.value)}}>
                    <option value={""}>Player</option>
                    {player.map((data) => (
                        <option key={data} value={data}>
                        {data}
                        </option>
                    ))}
                </select>
        </div>
    </div>
  )
}

export default Sports
