import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { validateUser, togglePasswordVisibility } from './loginValidation'; // Import external script
import './loginpage.css';

export default function Login() {
    const navigate = useNavigate();
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    
    function togglePasswordVisibility() {
        const passwordInput = document.querySelector("#txtPassword");
        if (passwordInput) {
            const type = passwordInput.getAttribute("type") === "password" ? "text" : "password";
            passwordInput.setAttribute("type", type);
        }
    }

    const handleLogin = async (e) => {
        e.preventDefault();
        const isValid = await validateUser(emailOrPhone, password);
        if (isValid) {
            navigate("/homepage");
        } else {
            setError("Invalid credentials. Please try again.");
        }
    };

    return (
        <div className="box">
            <form className="form" onSubmit={handleLogin}>
                <img src={""} alt="logo" />
                <h1 className="form-title">Sign In</h1><br />

                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control" 
                        required 
                        value={emailOrPhone}
                        onChange={(e) => setEmailOrPhone(e.target.value)}
                    />
                    <label className="form-label">Email or Phone</label>
                </div>
                <div className="form-group">
                    <input 
                        type="password" 
                        className="form-control" 
                        required 
                        id="txtPassword"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <label className="form-label">Enter Your Password</label>
                </div>
                <div>
                    <label className="showlabel">
                        Show Password
                        <input type="checkbox" id="show" onClick={togglePasswordVisibility} />
                    </label>            
                </div>

                {error && <p className="error-message">{error}</p>}

                <div className="bottom-box">
                    <Link to='/Signup'>New User</Link>
                    <button className="form-button" type="submit">Next</button>
                </div>
            </form>
        </div>
    );
}
