import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios';
import './signuppage.css';
import img from './images/loginpage.jpg';

export default function Signup() {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        password: '',
        confirmPassword: ''
    });
    const [errors, setErrors] = useState({});
    const [serverMessage, setServerMessage] = useState('');

    function togglePasswordVisibility() {
        setShowPassword(prevState => !prevState);
    }

    function handleChange(e) {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    }

    function validateForm() {
        let newErrors = {};

        if (!formData.name.trim()) newErrors.name = "Name is required";
        if (!formData.email.trim()) {
            newErrors.email = "Email is required";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "Invalid email format";
        }

        if (!formData.phone.trim()) {
            newErrors.phone = "Phone number is required";
        } else if (!/^\d+$/.test(formData.phone)) {
            newErrors.phone = "Phone number must contain only digits";
        } else if (formData.phone.length < 10) {
            newErrors.phone = "Phone number must be at least 10 digits";
        }

        if (!formData.password) {
            newErrors.password = "Password is required";
        } else if (formData.password.length < 6) {
            newErrors.password = "Password must be at least 6 characters";
        }

        if (!formData.confirmPassword) {
            newErrors.confirmPassword = "Please confirm your password";
        } else if (formData.confirmPassword !== formData.password) {
            newErrors.confirmPassword = "Passwords do not match";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    }

    async function handleSubmit(e) {
        e.preventDefault();
        if (!validateForm()) return;

        try {
            const response = await axios.post('http://localhost:5000/adduser', formData);
            setServerMessage(response.data.message || 'Registration successful!');
            setFormData({ name: '', email: '', phone: '', password: '', confirmPassword: '' });

            // Redirect after 2 seconds
            setTimeout(() => navigate('/'), 2000);
        } catch (error) {
            if (error.response) {
                // Backend errors
                setServerMessage(error.response.data.error || 'Something went wrong');
            } else {
                // Network errors
                setServerMessage('Failed to connect to the server');
            }
        }
    }

    return (
        <div className="box">
            <div className='login-image'>
                <img src={img} alt='log-img' />
            </div>
            <form className="form" onSubmit={handleSubmit}>
                <h1 className="form-title">Register User</h1>

                <div className="form-group">
                    <input type="text" name="name" className="form-control" value={formData.name} onChange={handleChange} />
                    <label className="form-label">Enter Your Name</label>
                    {errors.name && <p className="error-message">{errors.name}</p>}
                </div>

                <div className="form-group">
                    <input type="text" name="email" className="form-control" value={formData.email} onChange={handleChange} />
                    <label className="form-label">Enter Your Email</label>
                    {errors.email && <p className="error-message">{errors.email}</p>}
                </div>

                <div className="form-group">
                    <input type="text" name="phone" className="form-control" value={formData.phone} onChange={handleChange} 
                        onInput={(e) => {
                            e.target.value = e.target.value.replace(/\D/, '');
                            if (e.target.value.length > 10) { e.target.value = e.target.value.slice(0, 10); }
                        }} 
                    />
                    <label className="form-label">Enter Your Phone</label>
                    {errors.phone && <p className="error-message">{errors.phone}</p>}
                </div>

                <div className="form-group">
                    <input type={showPassword ? "text" : "password"} name="password" className="form-control" value={formData.password} onChange={handleChange} />
                    <label className="form-label">Enter Your Password</label>
                    {errors.password && <p className="error-message">{errors.password}</p>}
                </div>

                <div className="form-group">
                    <input type={showPassword ? "text" : "password"} name="confirmPassword" className="form-control" value={formData.confirmPassword} onChange={handleChange} />
                    <label className="form-label">Confirm Password</label>
                    {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>}
                </div>

                {serverMessage && <p className="server-message">{serverMessage}</p>}

                <div className='form-show'>
                    <Link to='/' className='Link'>Login</Link> 
                    <label className="showlabel">
                        <input type="checkbox" onChange={togglePasswordVisibility} checked={showPassword} />
                        Show Password
                    </label>  
                </div>

                <div className="bottom-box">
                    <button type="submit" className="form-button">Submit</button>
                </div>
            </form>
        </div>
    );
}
