import React, { useEffect, useState } from 'react'

const Sports = () => {
    const [fsport,setfsport]=useState("Select the favarout Sport");
    const [fplayer,setfplayer]=useState("Select the favarout player");
    const [player, setPlayer] = useState([]);

    const Players = {
        Cricket: ["Dhoni", "Virat Kohli", "Rohit Sharma"],
        Football: ["Messi", "Ronaldo", "Neymar"],
        Basketball: ["Jordan", "LeBron", "Kobe"],
      };
    // console.log(fsport)
    // console.log(fplayer)
    useEffect(()=>{
        if (fsport && Players[fsport]) {
            setPlayer(Players[fsport]);
            setfplayer(""); 
            console.log(players)
          } else {
            setPlayer([]);
          }
    },[fsport])
    useEffect(()=>{
        localStorage.setItem("sport",fsport) 
        localStorage.setItem("player",fplayer)
    },[fsport,fplayer])
    
    
  return (
    <div className='main'>
        <div>
            <div>
                <label>Favorite Sports</label>
                <input type='text'  id='sportsname' value={localStorage.getItem("sport")} readOnly></input>
                <label>Favorite Player</label>
                <input type='text'  id='playername' value={localStorage.getItem("player")} readOnly></input>
            </div>
            <div>
                <select id='fsprt' onChange={(e)=>{setfsport(e.target.value)}}>
                    <option value={""}>Sports</option>
                    <option value={"Cricket"}>Cricket</option>
                    <option value={"Football"}>Football</option>
                    <option value={"Basketball"}>Basketball</option>
                </select>
                <select id='fplyr' onChange={(e)=>{setfplayer(e.target.value)}}>
                    <option value={""}>Player</option>

                </select>
            </div>
        </div>
    </div>
  )
}

export default Sports
