import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
// import animeInfo from "./data";
import axios from "axios"; 
import "./card.css";
import Navbar from "./navbar";

function Info() {
  const location = useLocation();
  const animeId = location.state?.anime?.id; 
  const navigate = useNavigate();
  const [anime, setAnime] = useState(null);

  // const anime = animeInfo.find((a) => a.id === animeId);
  useEffect(() => {
    axios
      .get(`http://localhost:3001/getssingleanimebyid/${animeId}`)
      .then((res) => {
        setAnime(res.data);
      })
      .catch((error) => {
        console.error("Error fetching anime details:", error);
      });
  }, [animeId]);

  if (!anime) {
    return <h1 style={{ textAlign: "center" }}>No anime data available</h1>;
  }

  

  return (
    <div>
      <Navbar />
      <div style={{ textAlign: "center", padding: "20px" }}>
        <h1>{anime.title}</h1><br/>
        <img 
          src={require(`./images/${anime.image}`)} 
          alt={anime.title} 
          style={{ width: "300px", borderRadius: "10px" }} 
        />
        <br/>
        <p><strong>Episodes:</strong> {anime.episodes}</p><br/>
        <p><strong>Genres:</strong> {anime.genre.join(", ")}</p><br/>
        <p><strong>Description:</strong> {anime.description}</p><br/>

        {anime.watchLink && (
          <a href={anime.watchLink} target="_blank" rel="noopener noreferrer">
            <button className="watch-button">Watch Anime</button>
          </a>
        )}
        <br/><br/>

        <button onClick={() => navigate("/animepage")} className="info-button">Anime Page</button><br/><br/>

        <h1>Characters</h1><br/><br/>
        <div className="character-container">
          {anime.character.length > 0 ? (
            anime.character.map((char) => (
              <div key={char.id} className="card-info">
                <img 
                  src={require(`./images/${char.image}`)} 
                  alt={char.name} 
                  className="card-img" 
                />
                <h2 className="card-title">{char.name}</h2>
                {char.character && <h3 className="card-role">{char.character}</h3>}
              </div>
            ))
          ) : (
            <p>No characters found</p>
          )}
        </div>
      </div>
    </div>

  );
}

export default Info;
