import React from "react";
import { useLocation,useNavigate } from "react-router-dom";
import animeInfo from "./data"; 
import "./card.css"
import charactersInfo from "./character";

function Info() {
  const location = useLocation();
  const animeId = location.state?.anime?.id; 
  const characters = charactersInfo[animeId] || [];
  const navigate = useNavigate();
      function mainpage(){
          navigate("/homepage");
      }
  const anime = animeInfo.find((a) => a.id === animeId);
//   if (!anime) {
//     return <h1>No anime data available</h1>;
//   }
  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>{anime.title}</h1><br/>
      <img src={require(`./images/${anime.image}`)} alt={anime.title} style={{ width: "300px", borderRadius: "10px" }} /><br/>
      <p><strong>Episodes:</strong> {anime.episodes}</p><br/>
      <p><strong>Genres:</strong> {anime.genre.join(", ")}</p><br/>
      <p><strong>Description:</strong> {anime.description}</p><br/>
      <button onClick={mainpage} className="info-button">Main Page</button><br/>
      <h1>Characters</h1><></>
      <div>
        {characters.length > 0 ? (
          characters.map((char) => (
            <div key={char.id} className="card-info">
              <img src={require(`./images/${char.image}`)} alt={char.name} className="card-img" />
              <h2 className="card-title">{char.name}</h2>
              <h3 className="card-episodes">{char.character}</h3>
            </div>
          ))
        ) : (
          <p>No characters found</p>
        )}
      </div>
    </div>
  );
}

export default Info;
