// export default Info
import React from "react";
import { useLocation } from "react-router-dom";
import animeInfo from "./data"; // Import the anime details JSON file

function Info() {
  const location = useLocation();
  const animeId = location.state?.anime?.id; // Get the anime ID from navigation

  const anime = animeInfo.find((a) => a.id === animeId);

  if (!anime) {
    return <h1>No anime data available</h1>;
  }

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>{anime.title}</h1>
      <img src={anime.image} alt={anime.title} style={{ width: "300px", borderRadius: "10px" }} />
      <p><strong>Episodes:</strong> {anime.episodes}</p>
      <p><strong>Genres:</strong> {anime.genre.join(", ")}</p>
      <p><strong>Description:</strong> {anime.description}</p>
    </div>
  );
}

export default Info;
