import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import './signuppage.css';
import img from './images/loginpage.jpg';
// import users from './user'

export default function Signup() {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState({name: '',email: '',phone: '',password: '',confirmPassword: ''});
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);

    function togglePasswordVisibility() {
        setShowPassword(prevState => !prevState);
    }

    function handleChange(e) {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    }

    function validateForm() {
        let newErrors = {};

        if (!formData.name.trim()) {
            newErrors.name = "Name is required";
        }

        if (!formData.email.trim()) {
            newErrors.email = "Email is required";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "Invalid email format";
        }

        if (!formData.phone.trim()) {
            newErrors.phone = "Phone number is required";
        } else if (!/^\d+$/.test(formData.phone)) {
            newErrors.phone = "Phone number must contain only digits";
        } else if (formData.phone.length < 10) {
            newErrors.phone = "Phone number must be at least 10 digits";
        }

        if (!formData.password) {
            newErrors.password = "Password is required";
        } else if (formData.password.length < 4) {
            newErrors.password = "Password must be at least 6 characters";
        }

        if (!formData.confirmPassword) {
            newErrors.confirmPassword = "Please confirm your password";
        } else if (formData.confirmPassword !== formData.password) {
            newErrors.confirmPassword = "Passwords do not match";
        }

        // Check if the user already exists
        const userExists = users.some(
            user => user.email === formData.email || user.phone === formData.phone
        );

        if (userExists) {
            newErrors.userExists = "User with this email or phone number already exists";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    }

    async function handleSubmit(e) {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        setErrors({});

        try {
            const response = await axios.post("http://localhost:3001/signup", formData);
            
            if (response.data.success) {
                alert("Registration successful!");
                navigate('/'); 
            } else {
                setErrors({ userExists: response.data.message || "User already exists" });
            }
        } catch (error) {
            console.error("Signup Error:", error);
            setErrors({ server: "An error occurred. Please try again later." });
        }
        setLoading(false);
    }

    return (
        <div className="box">
            <div className='login-image'>
                <img src={img} alt='log-img' />
            </div>
            <form action="#" className="form" onSubmit={handleSubmit}>
                <h1 className="form-title">Register User</h1><br />

                <div className="form-group">
                    <input type="text" name="name" className="form-control" required value={formData.name} onChange={handleChange} />
                    <label className="form-label">Enter Your Name</label>
                    {errors.name && <p className="error-message">{errors.name}</p>}
                </div>

                <div className="form-group">
                    <input type="text" name="email" className="form-control" required value={formData.email} onChange={handleChange} />
                    <label className="form-label">Enter Your Email</label>
                    {errors.email && <p className="error-message">{errors.email}</p>}
                </div>

                <div className="form-group">
                    <input type="text" name="phone" className="form-control" required value={formData.phone} onChange={handleChange} onInput={(e) => {
                            e.target.value = e.target.value.replace(/\D/, '');
                            if (e.target.value.length > 10) { e.target.value = e.target.value.slice(0, 10);}}}/>
                    <label className="form-label">Enter Your Phone</label>
                    {errors.phone && <p className="error-message">{errors.phone}</p>}
                </div>

                <div className="form-group">
                    <input type={showPassword ? "text" : "password"} name="password" className="form-control" required value={formData.password} onChange={handleChange} />
                    <label className="form-label">Enter Your Password</label>
                    {errors.password && <p className="error-message">{errors.password}</p>}
                </div>

                <div className="form-group">
                    <input type={showPassword ? "text" : "password"} name="confirmPassword" className="form-control" required value={formData.confirmPassword} onChange={handleChange} />
                    <label className="form-label">Confirm Password</label>
                    {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>}
                </div>

                {errors.userExists && <p className="error-message">{errors.userExists}</p>}

                <br />

                <div className='form-show'>
                    <Link to='/' className='Link'>Login</Link> 
                    <label className="showlabel" htmlFor="show"> 
                        <input type="checkbox" id="show" onChange={togglePasswordVisibility} checked={showPassword} />
                        Show Password
                    </label>  
                </div><br />

                <div className="bottom-box" style={{ alignItems: "center" }}>
                    <button type="submit" className="form-button" disabled={loading}>Submit</button>
                </div>
            </form>
        </div>
    )
}
