// export default Homepage;
import React, { useEffect, useState } from 'react';
import Navbar from './navbar';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './homepage.css';

const Homepage = () => {
    const [username, setUsername] = useState("");
    const [trendingAnime, setTrendingAnime] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const User = localStorage.getItem("User");
        if (User) {
            setUsername(User);
        }
    }, []);

    useEffect(() => {
        axios.get("http://localhost:3001/getanimedetials")
            .then(response => {
                setTrendingAnime(response.data.documents.slice(0, 4));
            })
            .catch(error => console.error('Error fetching anime:', error));
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex(prevIndex => (prevIndex + 1) % trendingAnime.length);
        }, 3000);
        return () => clearInterval(interval);
    }, [trendingAnime]);

    const animepage = () => {
        navigate("/animepage");
    };

    return (
        <div className="homepage">
            <Navbar />
            <div className='first-box' style={{
                backgroundImage: trendingAnime.length > 0 ? `url(${require(`./images/${trendingAnime[currentIndex].image}`)})` : 'none',
            
            }}>
                <div className="header">
                    <h1>Welcome to My Anime Page{username && `, ${username}`}</h1>
                    <p>Explore the world of anime and discover new favorites!</p>
                    <button onClick={animepage} className='btn'>Explore More</button>
                </div>
            </div>
            <div className="trending-section">
                <h2>Trending Anime</h2>
                <div className="anime-list">
                    {trendingAnime.map((anime, index) => (
                        <div key={index} className="anime-card">
                            <img src={require(`./images/${anime.image}`)} alt={anime.title} />
                            <h3>{anime.title}</h3>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Homepage;
