export async function validateUser(emailOrPhone, password) {
    try {
        const response = await fetch('/users.json'); // Fetch user data
        const users = await response.json();

        // Check if user exists
        const user = users.find(user => 
            (user.email === emailOrPhone || user.phone === emailOrPhone) && user.password === password
        );

        return user ? true : false;
    } catch (error) {
        console.error("Error loading user data:", error);
        return false;
    }
}

// Toggle password visibility
export function togglePasswordVisibility() {
    const passwordInput = document.querySelector("#txtPassword");
    const type = passwordInput.getAttribute("type") === "password" ? "text" : "password";
    passwordInput.setAttribute("type", type);
}
