    if(state==20){
      alert("maximum incrementation reached")
    }
    else{
      setstate(state+1)
    }
  }
  function decrementa(){
    if(state<1){
      alert("can't be decreased")
    }
    else{
      setstate(state-1)
    }
  }
  function increment5(){
    if(add>99){
      alert("maximum incrementation reached")
    }
    else{
      add5(add+5)
    }
  }
  function decrement5(){
    if(add<1){
      alert("can't be decreased")
    }
    else{
      add5(add-5)
    }
  }
