  }
  return (
    <div className="App">
      <span id="a"  style={{textAlign:"center"}}>
        <h3 style={{ color: state >= 10 ? "red" : "black" }}>A={state}</h3>
      </span>
      <span style={{display: "flex", alignItems: "center", justifyContent: "center", gap: "10px"}}>
        <button onClick={incrementa}>incrementA</button>
        <button onClick={decrementa}>decrementA</button>
      </span>
      <br></br>
      <span id="a" style={{textAlign:"center"}}>
        <h3 style={{ color: add >= 20 ? "blue" : "black" }}>B={add}</h3>
      </span>
      <span style={{display: "flex", alignItems: "center", justifyContent: "center", gap: "10px"}}>
        <button onClick={increment5}>incrementB</button>
        <button onClick={decrement5}>decrementB</button>
      </span>
    </div>
  );
}

export default App;
