import React, { useEffect, useState } from "react";

const Demo = () => {
  const [fsport, setFsport] = useState("");
  const [fplayer, setFplayer] = useState("");
  const [players, setPlayers] = useState([]);

  // Define players based on the selected sport
  const sportsPlayers = {
    Cricket: ["Dhoni", "Kohli", "Sachin"],
    Football: ["Messi", "Ronaldo", "Neymar"],
    Basketball: ["Jordan", "LeBron", "Kobe"],
  };

  // Update the players dropdown when a sport is selected
  useEffect(() => {
    if (fsport && sportsPlayers[fsport]) {
      setPlayers(sportsPlayers[fsport]);
      setFplayer(""); // Reset player selection when sport changes
    } else {
      setPlayers([]);
    }
  }, [fsport]);

  // Save to localStorage when values change
  useEffect(() => {
    localStorage.setItem("sport", fsport);
    localStorage.setItem("player", fplayer);
  }, [fsport, fplayer]);

  return (
    <div className="main">
      <div>
        <label>Favorite Sport</label>
        <input type="text" id="sportsname" value={fsport} readOnly />

        <label>Favorite Player</label>
        <input type="text" id="playername" value={fplayer} readOnly />
      </div>
      <div>
        <select id="fsprt" onChange={(e) => setFsport(e.target.value)}>
          <option value="">Select Sport</option>
          <option value="Cricket">Cricket</option>
          <option value="Football">Football</option>
          <option value="Basketball">Basketball</option>
        </select>

        <select id="fplyr" onChange={(e) => setFplayer(e.target.value)} disabled={!fsport}>
          <option value="">Select Player</option>
          {players.map((player) => (
            <option key={player} value={player}>
              {player}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Demo;
