import React, { useEffect, useState } from 'react';

const Fontsize = () => {
    const [fontSize, setFontSize] = useState(20);
    const [color, setColor] = useState('black');
    const [bgcol,changebg] = useState('bisque');
    // useEffect(fontSize,[])
    function bgchange(){
        document.body.style.backgroundColor="bgcol"
    }
    return (
        <div className='main'>
            <div style={{display: "flex", alignItems: "center", justifyContent: "center" ,flexDirection:"column"}}>
                <span style={{display: "flex", alignItems: "center", justifyContent: "center"}} >
                <input type='range' min='20' max='100' value={fontSize} onChange={(e) => setFontSize(e.target.value)}  />
                </span>
                <br/>
                <br/>
                <span className='button' style={{display: "flex", alignItems: "center", justifyContent: "center", gap: "10px"}}> 
                    <button onClick={() => setColor('red')}>Red</button>
                    <button onClick={() => setColor('yellow')}>Yellow</button>
                    <button onClick={() => setColor('green')}>Green</button>
                </span>
                <br />
                <br />
                <p style={{textAlign:"center"}}>Chouse the background theam </p>
                <span className='radio' style={{display: "flex",alignItems: "center", justifyContent: "center", gap: "10px"}}>
                    <input type='radio' name='theam' value="black" onClick={() => changebg('black')}  />Dark  
                </span>
                <br />
                <span className='radio' style={{display: "flex",alignItems: "center", justifyContent: "center", gap: "10px"}}>
                    <input type='radio' name='theam' value="white" onClick={() => changebg('white')}  />Light
                </span>
            </div>
            <p style={{ fontSize:`${fontSize}px`, color ,textAlign:"center"}}>{fontSize}</p>
        </div>
    );
}

export default Fontsize;
