import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import Navbar from './navbar';
import { useNavigate } from 'react-router-dom';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './homepage.css';

const Homepage = () => {
    const [username, setUsername] = useState("");
    const [searchTerm, setSearchTerm] = useState("");
    const navigate = useNavigate();
    
    useEffect(() => {
        const User = localStorage.getItem("User");
        if (User) {
            setUsername(User);
        }
    }, []);

    const animepage = () => {
        navigate("/animepage"); 
    };

    const images = [
        "https://wallpaperaccess.com/full/767433.jpg",
        "https://wallpaperaccess.com/full/112192.jpg",
        "https://wallpaperaccess.com/full/384419.jpg"
    ];

    const trendingAnime = [
        { title: "Attack on Titan", image: "https://wallpaperaccess.com/full/552083.jpg" },
        { title: "Jujutsu Kaisen", image: "https://wallpaperaccess.com/full/4959186.jpg" },
        { title: "Demon Slayer", image: "https://wallpaperaccess.com/full/3444842.jpg" }
    ];

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        fade: true,
    };

    return (
        <div className="homepage">
            <Navbar />
            <div className="search-container">
                <input 
                    type="text" 
                    placeholder="Search anime..." 
                    value={searchTerm} 
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                />
            </div>
            <div className="carousel-container">
                <Slider {...settings} className="carousel">
                    {images.map((img, index) => (
                        <div key={index} className="slide">
                            <img src={img} alt={`Anime ${index + 1}`} className="carousel-image" />
                        </div>
                    ))}
                </Slider>
                <div className="header">
                    <h1>Welcome to My Anime Page{username && `, ${username}`}</h1>
                    <p>Explore the world of anime and discover new favorites!</p>
                </div>
            </div>
            <div className="trending-section">
                <h2>Trending Anime</h2>
                <div className="trending-anime">
                    {trendingAnime.map((anime, index) => (
                        <div key={index} className="anime-card">
                            <img src={anime.image} alt={anime.title} />
                            <p>{anime.title}</p>
                        </div>
                    ))}
                </div>
            </div>
            <div className="button-container">
                <button onClick={animepage} className='btn'>Explore Anime</button>
            </div>
        </div>
    );
};

export default Homepage;
