<div>
  <Navbar />
  <div className="anime-card">
    <h1 className="anime-title">{anime.title}</h1>
    <div className="anime-container">
      <div className="anime-image">
        <img 
          src={require(`./images/${anime.image}`)} 
          alt={anime.title} 
        />
      </div>
      <div className="anime-details">
        <p><strong>Episodes:</strong> {anime.episodes}</p>
        <p><strong>Genres:</strong> {anime.genre.join(", ")}</p>
        <p><strong>Description:</strong> {anime.description}</p>

        {anime.watchLink && (
          <a href={anime.watchLink} target="_blank" rel="noopener noreferrer">
            <button className="watch-button">Watch Anime</button>
          </a>
        )}

        <button onClick={() => navigate("/animepage")} className="info-button">Anime Page</button>
      </div>
    </div>
  </div>

  <h1 className="characters-title">Characters</h1>
  <div className="character-container">
    {anime.character.length > 0 ? (
      anime.character.map((char) => (
        <div key={char.id} className="card-info">
          <img 
            src={require(`./images/${char.image}`)} 
            alt={char.name} 
            className="card-img" 
          />
          <h2 className="card-title">{char.name}</h2>
          {char.character && <h3 className="card-role">{char.character}</h3>}
        </div>
      ))
    ) : (
      <p>No characters found</p>
    )}
  </div>
</div>

  );
}

export default Info;
