// export default Profile;
import React, { useEffect, useState } from 'react';
import Navbar from './navbar';
import users from './user.js';
import backgroundImage from './images/homepage.png';
import './profile.css';

const Profile = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [editable, setEditable] = useState(false);
    const [updatedUser, setUpdatedUser] = useState({
        name: "",
        email: "",
        phone: ""
    });

    useEffect(() => {
        const User = localStorage.getItem("User");
        if (User) {
            const user = users.find(u => u.name === User);
            if (user) {
                setLoggedInUser(user);
                setUpdatedUser(user);
            }
        }
    }, []);

    const handleChange = (e) => {
        setUpdatedUser({ ...updatedUser, [e.target.name]: e.target.value });
    };

    const handleUpdate = () => {
        setEditable(!editable);

        if (editable) {
            setLoggedInUser(updatedUser);
            localStorage.setItem("User", updatedUser.name);
            alert("Profile updated successfully!");
        }
    };

    return (
        <div className="profile" style={{ backgroundImage: `url(${backgroundImage})` }}>
            <Navbar />
            <div className="profile-container">
                <h2>User Info</h2>
                {loggedInUser ? (
                    <form className="user-details">
                        <label>Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={updatedUser.name}
                            onChange={handleChange}
                            readOnly={!editable}
                        />

                        <label>Email:</label>
                        <input
                            type="text"
                            name="email"
                            value={updatedUser.email}
                            onChange={handleChange}
                            readOnly={!editable}
                        />

                        <label>Phone:</label>
                        <input
                            type="text"
                            name="phone"
                            value={updatedUser.phone}
                            onChange={handleChange}
                            readOnly={!editable}
                        />

                        <button type="button" className="btn" onClick={handleUpdate}>
                            {editable ? "Save" : "Update Details"}
                        </button>
                    </form>
                ) : (
                    <p className="error-msg">No user logged in</p>
                )}
            </div>
        </div>
    );
};

export default Profile;
