import React, { useEffect, useState } from "react";
import "./Sports.css";

const Sports = () => {
  const Players = {
    Cricket: ["Dhoni", "Virat Kohli", "Rohit Sharma"],
    Football: ["Messi", "Ronaldo", "Neymar"],
    Basketball: ["Jordan", "LeBron", "Kobe"],
  };

  const [fsport, setFsport] = useState(localStorage.getItem("sport") || "");
  const [fplayer, setFplayer] = useState(localStorage.getItem("player") || "");
  const [player, setPlayer] = useState([]);

  useEffect(() => {
    if (fsport && Players[fsport]) {
      setPlayer(Players[fsport]);
      if (!Players[fsport].includes(fplayer)) {
        setFplayer("");
      }
    } else {
      setPlayer([]);
      setFplayer("");
    }
  }, [fsport]);

  useEffect(() => {
    localStorage.setItem("sport", fsport);
    localStorage.setItem("player", fplayer);
  }, [fsport, fplayer]);

  return (
    <div className="main">
      <div className="box">
        <label>Favorite Sport</label>
        <input type="text" value={fsport} placeholder="Select Your Favorite Game" readOnly />

        <label>Favorite Player</label>
        <input type="text" value={fplayer} placeholder="Select Your Favorite Player" readOnly />

        <select value={fsport} onChange={(e) => setFsport(e.target.value)}>
          <option value="">Select Sport</option>
          <option value="Cricket">Cricket</option>
          <option value="Football">Football</option>
          <option value="Basketball">Basketball</option>
        </select>

        <select value={fplayer} onChange={(e) => setFplayer(e.target.value)}>
          <option value="">Select Player</option>
          {player.map((data) => (
            <option key={data} value={data}>
              {data}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Sports;
