import { useState } from "react";

function App() {
  const [state,setstate]=useState(1)
  const [add,add5]=useState(5)
  function increment(){
    if(state==20){
      alert("maximum incrementation reached")
    }
    else{
      setstate(state+1)
    }
  }
  function decrement(){
    if(state==0){
      alert("can't be decreased")
    }
    else{
      setstate(state-1)
    }
  }
  function increment5(){
    if(state==100){
      alert("maximum incrementation reached")
    }
    else{
      setstate(add5+5)
    }
  }
  function decrement5(){
    if(state==0){
      alert("can't be decreased")
    }
    else{
      setstate(add5-5)
    }
  }
  return (
    <div className="App">
      <h1>Hi Ganesh</h1>
      <span style={{alignItems:"center",justifyContent:"center"}}>
        <h3>a={state}</h3>
      </span>
      <button onClick={increment}>increment</button>
      <button onClick={decrement}>decrement</button>
      <span style={{alignItems:"center",justifyContent:"center"}}>
        <h3>a={state}</h3>
      </span>
      <button onClick={increment}>increment</button>
      <button onClick={decrement}>decrement</button>
    </div>
  );
}

export default App;
